using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace AoC.Days
{
    class Day2
    {
        static string input = @"



";

        public string Part1()
        {
            var answer = "";
            var Position = 5;
            var instructions = input.Split(new string[] { Environment.NewLine }, StringSplitOptions.None);
            foreach (string instruction in instructions)
            {
                foreach (char c in instruction)
                {
                    Position = Move(c, Position);
                }
                answer += Position.ToString();
            }
            return "Bathroom code: " + answer;
        }

        private int Move(char direction, int position)
        {
            switch (direction)
            {
                case 'U':
                    if (position <= 4)
                    {
                        return position;
                    }
                    else
                    {
                        return position - 3;
                    }
                case 'D':
                    if (position >= 7)
                    {
                        return position;
                    }
                    else
                    {
                        return position + 3;
                    }
                case 'L':
                    if (position == 1 || position == 4 || position == 7)
                    {
                        return position;
                    }
                    else
                    {
                        return position - 1;
                    }
                case 'R':
                    if (position == 3 || position == 6 || position == 9)
                    {
                        return position;
                    }
                    else
                    {
                        return position + 1;
                    }
            }
            return position;
        }
    }
}
